import styled from "styled-components";

export const STop = styled.div`
  color: #FFFFFF;
  padding: 20px;
  display: flex;

  p {
    margin: 0;
  }

  img {
    width: 14px;
    height: 14px;
    color: #FFFFFF;
  }

  svg {
    margin-right: 7px;
  }
`

export const SSection = styled.div`
  color: #9C5AF2;
  padding: 0 20px;
  margin-bottom: 20px;
  display: flex;

  p {
    margin: 0;
    align-items: center;
    margin-left: 5px;
  }

  svg {
    margin-left: -12px;
  }

  .left {
    width: 35%;
    img {
      width: 150px;
      height: 150px;
    }
  }

  .right {
    width: 65%;
  }
`
