import styled from "styled-components";

export const STop = styled.div`
  color: #FFFFFF;
  padding: 20px;
  display: flex;

  p {
    margin: 0;
  }

  img {
    width: 14px;
    height: 14px;
    color: #FFFFFF;
  }

  svg {
    margin-right: 7px;
  }
`

export const SSection = styled.div`
  color: #9C5AF2;
  padding: 0 20px;
  margin-bottom: 20px;
  display: flex;

  p {
    margin: 0;
    align-items: center;
    margin-left: 5px;
  }

  svg {
    margin-left: -12px;
  }

  .left {
    width: 35%;
    img {
      min-width: 100%;
      min-height: 140px;
      border: 1px solid #FFFFFF;
      background-color: #FFFFFF;
    }
  }

  .right {
    width: 65%;

    h1 {
      margin: 0;
      padding: 0 10px;
      color: #FFFFFF;
      font-weight: 400;
    }

    p {
      margin-left: 10px;
    }

    div {
      margin: 0 10px;
      display: flex;
      padding-top: 54px;
      justify-content: space-between;
    }

    .button1 {
      width: 128px;
      height: 31px;
      color: #FFFFFF;
      background-color: #9C5AF2;
      border-radius: 20px;
      font-size: 16px;
      border: none;
    }

    .button2 {
      width: 31px;
      height: 31px;
      color: #FFFFFF;
      background-color: #9C5AF2;
      border-radius: 50%;
      font-size: 16px;
      border: none;
      font-weight: 800;
    }
  }
`

export const SSection2 = styled.div`
  color: #8D8F93;
  padding: 0 20px;
  margin-bottom: 20px;
  display: flex;
  justify-content: space-between;

  p {
    margin: 0 0 5px 0;
    font-size: 22px;
  }

  span {
    font-size: 14px;
  }
`

export const SSection3 = styled.div`
  color: #DADBDB;
  padding: 0 20px;
  margin-bottom: 20px;
  display: block;
  font-size: 16px;

  p {
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  input[name="toggle"]:checked {
    & + p {
      -webkit-line-clamp: unset;
    }
  }

  label {
    &::after {
      content: "显示更多";
    }
  }
`
