import React from "react";
import { NextPage } from "next";
import matter from "gray-matter";
import ReactMarkdown from "react-markdown";

type Props = {
  content: string;
  data: {
    title: string;
    data: Date;
  };
};

const Page: NextPage<Props, any> = ({ content, data = {} }) => {
  return (
    <>
      <div>This is my article !</div>
      <article>
        <ReactMarkdown 
          escapeHtml={true}
          source={content}
        />
      </article>
    </>
  );
};

Page.getInitialProps = async (context) => {
  // 1. 获取 Slug
  const { slug, category } = context.query;

  // 3. markdown to json 
  try {
    const fileData = await import(`../../_posts/${category}/${slug}.md`);
    const { data, content} = matter(fileData.default);
    return {
      namespacesRequired: ["common"],
      data,
      content,
    };
  }catch (error) {
    return {
      namespacesRequired: ["common"],
      content: undefined,
    };
  }
};

export default Page as any;
