import styled from "styled-components";

export const SSection = styled.section`
  width: 100%;
  padding: 125px 0;
  display: block;
  overflow: hidden;
  background: 0 0;
`;

export const SDetail = styled.div`
  width: 100%;
  height: auto;
  margin: 0 auto;
  padding: 10% 20% 20% 20%;
  background-color: #fff;
  font-size: 1.2em;
  font-weight: 150;
  text-align: center;
  top: 50%;
  left: 0;
  transform: translate3d(0, -50%, 0);
  opacity: .75;
`;
